CREATE TABLE Allergens
(
    Shortname VARCHAR(1) PRIMARY KEY,
    Fullname  TEXT
);

CREATE TABLE People
(
    id        INT AUTO_INCREMENT PRIMARY KEY,
    Firstname TEXT,
    Lastname  TEXT,
    DOB       DATE
);

CREATE TABLE PictureFiles
(
    id        INT AUTO_INCREMENT PRIMARY KEY,
    Bytes     TEXT,
    Name      TEXT,
    MediaType TEXT
);

CREATE TABLE Foods
(
    id        INT AUTO_INCREMENT PRIMARY KEY,
    Name      TEXT,
    PictureID INT,
    Type      TEXT
);

CREATE TABLE FoodAllergens
(
    id                INT AUTO_INCREMENT PRIMARY KEY,
    AllergenShortname TEXT,
    FoodId            INT
);

