CREATE TABLE SpecialMeals
(
    Date           DATE NOT NULL PRIMARY KEY, -- Datum als Primärschlüssel
    SoupID         INT,                       -- Fremdschlüssel zur Tabelle Foods
    M1ID           INT,                       -- Fremdschlüssel zur Tabelle Foods
    M2ID           INT,                       -- Fremdschlüssel zur Tabelle Foods
    LunchDessertID INT,                       -- Fremdschlüssel zur Tabelle Foods
    A1ID           INT,                       -- Fremdschlüssel zur Tabelle Foods
    A2ID           INT,                       -- Fremdschlüssel zur Tabelle Foods
    FOREIGN KEY (SoupID) REFERENCES Foods (ID) ON DELETE RESTRICT,
    FOREIGN KEY (M1ID) REFERENCES Foods (ID) ON DELETE RESTRICT,
    FOREIGN KEY (M2ID) REFERENCES Foods (ID) ON DELETE RESTRICT,
    FOREIGN KEY (LunchDessertID) REFERENCES Foods (ID) ON DELETE RESTRICT,
    FOREIGN KEY (A1ID) REFERENCES Foods (ID) ON DELETE RESTRICT,
    FOREIGN KEY (A2ID) REFERENCES Foods (ID) ON DELETE RESTRICT
);

CREATE TABLE Menu (
                      ID INT AUTO_INCREMENT PRIMARY KEY,
                      WeekNumber INT NOT NULL,
                      Weekday VARCHAR(50) NOT NULL,
                      SoupID INT,
                      M1ID INT,
                      M2ID INT,
                      LunchDessertID INT,
                      A1ID INT,
                      A2ID INT,
                      FOREIGN KEY (SoupID) REFERENCES Foods(ID),
                      FOREIGN KEY (M1ID) REFERENCES Foods(ID),
                      FOREIGN KEY (M2ID) REFERENCES Foods(ID),
                      FOREIGN KEY (LunchDessertID) REFERENCES Foods(ID),
                      FOREIGN KEY (A1ID) REFERENCES Foods(ID),
                      FOREIGN KEY (A2ID) REFERENCES Foods(ID)
);

CREATE TABLE Orders
(
    ID               INT AUTO_INCREMENT PRIMARY KEY,
    Date             DATE NOT NULL,
    UserID           INT  NOT NULL,
    MenuID           INT  NOT NULL,
    DessertSelected  INT,
    SelectedLunchID  INT,
    SelectedDinnerID INT,
    ordered_at       TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (UserID) REFERENCES People (ID) ON DELETE CASCADE,
    FOREIGN KEY (MenuID) REFERENCES Menu (ID) ON DELETE CASCADE,
    FOREIGN KEY (SelectedLunchID) REFERENCES Foods (ID) ON DELETE SET NULL,
    FOREIGN KEY (SelectedDinnerID) REFERENCES Foods (ID) ON DELETE SET NULL
);

